<template lang="pug">
  .floating-menu
    .floating-menu__list.active(@click="changeMenu(`earn`)")
      <svg xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" width="25" height="20">
        <image width="25" height="20" xlink:href="data:img/png;base64,"/>
      </svg>
      p Earn
    .floating-menu__list(@click="changeMenu(`rent`)")
      <svg xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" width="24" height="20">
        <image width="24" height="20" xlink:href="data:img/png;base64,"/>
      </svg>
      p Rent
    .floating-menu__list(@click="changeMenu(`market`)")
      <svg xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" width="20" height="20">
        <image width="20" height="20" xlink:href="data:img/png;base64,"/>
      </svg>
      p Market
    .floating-menu__content.active#earn
      p Lorem Ipsum Dolor Sit Amet
      p Didunt ut labore et dolore magna aliqua.
      button.button.button--black.button--radius-25.button--100 Register
    .floating-menu__content#rent
      p Lorem Ipsum Dolor Sit Amet
      p Didunt ut labore et dolore magna aliqua.
      button.button.button--black.button--radius-25.button--100 Hello
    .floating-menu__content#market
      p Lorem Ipsum Dolor Sit Amet
      p Didunt ut labore et dolore magna aliqua.
      button.button.button--black.button--radius-25.button--100 Hi
</template>

<script>
export default {
  name: "floating_menu",
  methods: {
    changeMenu(pickedMenu) {
      var lists = document.getElementsByClassName("floating-menu__list");
      for (let i = 0; i < lists.length; i++) {
        lists[i].className = lists[i].className.replace(" active", "");
      }

      switch (pickedMenu) {
        case "earn":
          lists[0].classList.add("active");
          break;
        case "rent":
          lists[1].classList.add("active");
          break;
        case "market":
          lists[2].classList.add("active");
          break;
        default:
          break;
      }

      var contents = document.getElementsByClassName("floating-menu__content");
      for (let i = 0; i < contents.length; i++) {
        contents[i].style.display = "none";
      }

      document.getElementById(pickedMenu).style.display = "block";
    }
  }
};
</script>
